package ru.guap.player.model;import java.io.File;import java.util.*;public class Playlist {	private List < File > myPlaylist;	private int myCurr;		Playlist () {		myPlaylist = new LinkedList < File > ();		myCurr = 0;	}		public void add ( File file ) {		myPlaylist.add ( file );	}		public boolean next () {		return ( (myCurr = ++myCurr % myPlaylist.size ()) == 0 ? false : true  );	}		public boolean previous () {		return ( (myCurr = --myCurr % myPlaylist.size ()) == myPlaylist.size () - 1 ? false : true  );	}		public File getCurrent () {		return ( myPlaylist.size () == 0 ? null : myPlaylist.get ( myCurr ) );	}		public String[] getList () {		List < String > res = new ArrayList ();				for ( File f: myPlaylist ) {			res.add ( f.getName () );		}				return res.toArray ( new String[ 0 ] );	}}