import java.net.URL;import javax.media.ControllerEvent;import javax.media.ControllerListener;import javax.media.EndOfMediaEvent;import javax.media.Manager;import javax.media.MediaLocator;import javax.media.Player;import java.io.*;public class test extends Thread {   private String filename;   Player player;   public test(String mp3Filename) {      filename = mp3Filename;   }	public void run() {		try {			File f = new File ( filename );			System.out.println ( f.toURL() );			MediaLocator locator = new MediaLocator( f.toURL() );			player = Manager.createPlayer( locator );			player.addControllerListener(new ControllerListener() {				public void controllerUpdate(ControllerEvent event) {					System.out.println ( event );					if (event instanceof EndOfMediaEvent) {						player.stop();						player.close();					}				}			});			player.realize();			player.start();			System.out.println ( "Started..." );		} catch (Exception e) {			e.getMessage();                                                                                                                                                                                                      			e.printStackTrace();		}	}   public static void main(String[] args) {		try {			Thread t = new test("a.mp3");			t.start();			t.join();			System.out.println ( "Successful ended!" );		} catch ( Exception e ) {			System.out.println ( e.getMessage() );		}   }}